//AMXSXMAAMM
//MSAMASMSMX
//XMASAMXAMM
//XXAMMXXAMA
//SMSMSASXSS
//SAXAMASAAA
//MAMMMXMMMM
//MXMXAXMASX
//"""

let linesArray = input.components(separatedBy: "\n")
func convertTo2DArray(array: [String]) -> [[Character]] {
    var input2DArray:[[Character]] = []
    array.forEach { value in
        input2DArray.append(value.map {$0})
    }
    return input2DArray
}

func findXShape(grid: [[Character]]) -> Int {
    let rows = grid.count
    let cols = grid[0].count
    var count = 0
    var markedGrid = Array(repeating: Array(repeating: Character("."), count: cols), count: rows)
    for i in 1..<(rows - 1) {
        for j in 1..<(cols - 1) {
            // Check for "B" as center of the "X"
            if grid[i][j] == "A" {
                let isXMAS = (
                    ((grid[i-1][j-1] == "M" && grid[i+1][j+1] == "S") || (grid[i-1][j-1] == "S" && grid[i+1][j+1] == "M")) &&
                    ((grid[i-1][j+1] == "M" && grid[i+1][j-1] == "S") || grid[i-1][j+1] == "S" && grid[i+1][j-1] == "M"))

                if isXMAS {
                    markedGrid[i][j] = grid[i][j]
                    markedGrid[i-1][j-1] = grid[i-1][j-1]
                    markedGrid[i+1][j+1] = grid[i+1][j+1]
                    markedGrid[i-1][j+1] = grid[i-1][j+1]
                    markedGrid[i+1][j-1] = grid[i+1][j-1]
                    count += 1
                }
            }
        }
    }
    for row in markedGrid {
        print(String(row))
    }
    return count
}
let input2dArray = convertTo2DArray(array: linesArray)
print(findXShape(grid: input2dArray))
